cbo_update()
{

  int i;                   
  string    dpType;
  dyn_string DPTs, ds, dps;  
  bool bMPExists;

   dpType = &quot;FILTR_ALARMS&quot;;

  DPTs = strsplit(dpType, &quot;,&quot;);

 // DebugN(DPTs);

  for (i= 1; i&lt;= dynlen(DPTs);i++)
 { 
 ds = dpNames(&quot;*&quot;,DPTs[i]);
dynAppend(dps,ds);    
acGetReferences(DPTs[i], dps);
if (!bMPExists)
bMPExists=dpExists(&quot;_mp_&quot;+DPTs[i]);
}
  for (i=dynlen(dps); i&gt;=1 ;i--)
 { 
strreplace (dps[i], getSystemName(), &quot;&quot;);
if (strpos(dps[i],&quot;_mp_&quot;)==0 || strpos(dps[i],&quot;:_mp_&quot;)&gt;0)
{
  dynRemove(dps,i);
}
}

  cboSelect.items = dps; 

}

check_filtr()
    {

  string dp_alerts_filtr = g_propDpNameTop + &quot;.Alerts.Filter.Prio&quot;;
  bool state_info, state_warning, state_alarm;
  string warning, info, alerts, filtr;
  getMultiValue( &quot;CHECK_INFO_ALARM&quot;, &quot;state&quot;, 0, state_info,
                 &quot;CHECK_WARNING_ALARM&quot;, &quot;state&quot;, 0, state_warning,
                 &quot;CHECK_ALERT_ALARM&quot;, &quot;state&quot;, 0, state_alarm);
  if (state_info)
  {
    if (state_warning || state_alarm)
      info = &quot;10,&quot;;
  else 
   info = &quot;10&quot;; 
  }
  else
  {    info = &quot;&quot;; }

    if (state_warning)
  {
    if  (state_alarm)
      warning = &quot;40,&quot;;
  else 
   warning = &quot;40&quot;; 
  }
      else
    warning = &quot;&quot;; 

if (state_alarm)
   alerts = &quot;60,80&quot;; 
else 
   alerts = &quot;&quot;;  

 filtr = info + warning + alerts;
dpSetWait(dp_alerts_filtr, filtr);

  aes_doStop( g_propDpNameTop );
  aes_doStart( g_propDpNameTop );

}
