string stsToText(int sts){
  string res;
  switch(sts){
    case 0x00:
      res = &quot;Ожидание&quot;;
      break;
    case 0x10:
      res = &quot;Разрешение налива&quot;;
      break;
    case 0x20:
      res = &quot;Налив&quot;;
      break;
    case 0x30:
      res = &quot;Пауза&quot;;
      break;
    case 0x40:
      res = &quot;Продолжение&quot;;
      break;
    case 0x60:
      res = &quot;Ошибка&quot;;
      break;
    case 0x80:
      res = &quot;Не активный&quot;;
      break;
    default:
      res = &quot;Неизвестно&quot;;
  }
  return res;
}

string stsToColor(int sts){
  string res;
  switch(sts){
    case 0x00:
      res = &quot;Yellow&quot;;
      break;
    case 0x10:
      res = &quot;Yellow&quot;;
      break;
    case 0x20:
      res = &quot;Green&quot;;
      break;
    case 0x30:
      res = &quot;Yellow&quot;;
      break;
    case 0x40:
      res = &quot;Green&quot;;
      break;
    case 0x60:
      res = &quot;Red&quot;;
      break;
    case 0x80:
      res = &quot;Red&quot;;
      break;
    default:
      res = &quot;Red&quot;;
  }
  return res;
}

