main(){
  string type;
  int type_int;
  float pris;
  dpGet(DP + &quot;.Type_NP&quot;, type_int,
        DP + &quot;.cPercentPrisadki&quot;, pris);

  //Отключил кнопку &quot;Продолжить&quot;, водитель сам должен двойным нажатием кнопки переводить пост из Паузы в Продолжение и из Продолжения в Пуск
  PANEL_REF16.visible(false);

  sdShowValue(&quot;txt_pris_percent&quot;, DP + &quot;.cPercentPrisadki&quot;, pris, 2, &quot;0.123456&quot;, 2, &quot;%&quot;);
  string str_pris = ValToFormatedString(DP + &quot;.cPercentPrisadki&quot;, pris);
  NOM_POST.text = &quot;ПОСТ № &quot;+strltrim(dpSubStr(DP, DPSUB_DP), &quot;Post_&quot;);
  if((float)str_pris == OPTI){
    prisadka.currentIndex(0);
  }else if((float)str_pris == GDRIVE){
    prisadka.currentIndex(1);
  }else if((float)str_pris == KMH){
    prisadka.currentIndex(2);
  }else{
    prisadka.currentIndex(3);
  }
   switch(type_int){
    case 0:
      type = &quot;АИ-92&quot;;
      break;
    case 1:
      type = &quot;АИ-95&quot;;
      break;
    case 2:
      type = &quot;АИ-98&quot;;
      break;
    case 3:
      type = &quot;АИ-100&quot;;
      break;
    case 4:
      type = &quot;ДТ&quot;;
      break;
    default:
      type = &quot;---&quot;;
  }
//   txt_type_np.text(type);

  /**dpConnect(&quot;cb_sts_stop_err_reason&quot;, DP + &quot;.sStatusStop&quot;,
                                      DP + &quot;.sErrorNaliv&quot;);*/


  dpConnect(&quot;cb_post_params&quot;, DP + &quot;.sStatusPosta&quot;,                        //1
                              DP + &quot;.Sensor_Control.sSensorGround&quot;,        //2
                              DP + &quot;.Sensor_Control.sSensorOverflow&quot;,      //3
                              DP + &quot;.Sensor_Control.sGaragePosition&quot;,      //4
                              DP + &quot;.Sensor_Control.sSensorWorkPosition&quot;,  //5
                              DP + &quot;.Sensor_Control.sSensorTrapa&quot;,         //6
                              DP + &quot;.Sensor_Control.sSensorProduct&quot;,       //7
                              DP + &quot;.Sensor_Control.sSensorFilter&quot;,        //8
                              DP + &quot;.Sensor_Control.sFlowLL&quot;,              //9
                              DP + &quot;.Sensor_Control.sRiserTiltSensor&quot;,     //10
                              DP + &quot;.Sensor_Control.sButtonStartStop&quot;);    //11

}

main()
{
//   if(g_first){
//     DebugN(&quot;FIRST START ASN FP&quot;);
//   }else{
    if(this.currentIndex() == 0){
      txt_pris_percent.text(OPTI + &quot;%&quot;);
      dpSet(DP + &quot;.cPercentPrisadki&quot;, OPTI);
    }else if(this.currentIndex() == 1){
      txt_pris_percent.text(GDRIVE + &quot;%&quot;);
      dpSet( DP + &quot;.cPercentPrisadki&quot;, GDRIVE);
    }else if(this.currentIndex() == 2){
      txt_pris_percent.text(KMH + &quot;%&quot;);
      dpSet( DP + &quot;.cPercentPrisadki&quot;, KMH);
    }else{
      txt_pris_percent.text(DEFPR + &quot;%&quot;);
      dpSet( DP + &quot;.cPercentPrisadki&quot;, DEFPR);
    }
//   }
//   g_first = false;
}

