void worker(dyn_string dp, dyn_anytype val){
  radio_mode.number(val[1]);
  current_card.text(val[2]);
  txt_sRegANr.text(val[3]);

  sc1.setEnable(!val[1]);
  sc2.setEnable(!val[1]);
  sc3.setEnable(!val[1]);
  sc4.setEnable(!val[1]);
  sc5.setEnable(!val[1]);
  sc6.setEnable(!val[1]);
  sc7.setEnable(!val[1]);
  sc8.setEnable(!val[1]);
  sc9.setEnable(!val[1]);
  sc10.setEnable(!val[1]);
  btn_start.enabled(!val[1]);
  btn_clr.enabled(!val[1]);
}

void worker_post(dyn_string dp, dyn_anytype val){
  TABLE4.deleteAllLines();
  for(int i=1; i&lt;=val.count(); i+=2){
    string tmp = strltrim(dpSubStr(dp[i], DPSUB_DP),&quot;Post_&quot;);
    TABLE4.appendLine(&quot;#1&quot;, tmp, &quot;stpCell&quot;, stpToText(val[i+1]), &quot;alrCell&quot;, errToText(val[i]));
  }
}

main(){
  Name_line.text = &quot;Линия налива №&quot; + LINE;
  dpConnect(&quot;worker&quot;, &quot;ORDER_LINE&quot; + LINE + &quot;.control&quot;,
                      &quot;ORDER_LINE&quot; + LINE + &quot;.current_card&quot;,
                      &quot;ORDER_LINE&quot; + LINE + &quot;.sRegNr&quot;);
  dyn_int posts;
  dpGet(&quot;ORDER_LINE&quot; + LINE + &quot;.local.HelperPosts&quot;, posts);
  dyn_string dp_posts;
  for(int i=1; i&lt;=posts.count(); i++){
    dp_posts.append(&quot;Post_&quot; + posts[i] + &quot;.sStatusPosta&quot;);
    dp_posts.append(&quot;Post_&quot; + posts[i] + &quot;.sStatusStop&quot;);
  }
  dpConnect(&quot;worker_post&quot;, dp_posts);
  txt_sRegANr.enabled = getUserPermission(17);
  current_card.enabled = getUserPermission(17);
  radio_mode.enabled = getUserPermission(17);

}

extended.script:
void clickedBtn()
{
  anytype card, regNr;
  dpGet(&quot;ORDER_LINE&quot; + LINE + &quot;.current_card&quot;, card,
        &quot;ORDER_LINE&quot; + LINE + &quot;.sRegNr&quot;      , regNr);
  dyn_string fuels = makeDynString(sc1.getFuel(), sc2.getFuel(), sc3.getFuel(), sc4.getFuel(), sc5.getFuel(),
                                   sc6.getFuel(), sc7.getFuel(), sc8.getFuel(), sc9.getFuel(), sc10.getFuel());
  for(int i=1; i&lt;=fuels.count(); i++){
    if(fuels[i] != &quot;НЕТ&quot;){
      dpSetWait(&quot;ORDER_LINE&quot; + LINE + &quot;.items.&quot; + i + &quot;.init.sRegNr&quot;, regNr);
    }
  }
// Запуск задания в ручном режиме
  dpSetWait(&quot;ORDER_LINE&quot; + LINE + &quot;.current_card&quot;, card,
            &quot;ORDER_LINE&quot; + LINE + &quot;.iProcessed&quot;, 1);
}
