events.script:
const string DP = $dp;
const string LogicRVS = $dpLogicRVS;
const string LS1 = $ls1;
const string LS2 = $ls2;
const string LS3 = $ls3;
const string NAME = $name;
const string FUEL = $fuel;

void worker_val(dyn_string dp, dyn_anytype val){
  changeLVL.foreCol(val[11]);

  sdScaleVertical(&quot;top_bar&quot;, val[2], val[10], val[9]);
}

void cbLogicRVS(string dpe, int val) {
  // storage=1|taking=2|release=3|maint=6
  switch (val){
      case 1:
        btn_taking.setAct(false);
        btn_release.setAct(false);
        btn_maint.setAct(false);
        btn_storage.setAct(true);
        break;
      case 2:
        btn_taking.setAct(true);
        btn_release.setAct(false);
        btn_maint.setAct(false);
        btn_storage.setAct(false);
        break;
      case 3:
        btn_taking.setAct(false);
        btn_release.setAct(true);
        btn_maint.setAct(false);
        btn_storage.setAct(false);
        break;
      case 6:
        btn_taking.setAct(false);
        btn_release.setAct(false);
        btn_maint.setAct(true);
        btn_storage.setAct(false);
        break;
      default:
        btn_taking.setAct(false);
        btn_release.setAct(false);
        btn_maint.setAct(false);
        btn_storage.setAct(false);
    }
}

void cb_dt(dyn_string dp, dyn_anytype val){
  for(int i=1; i&lt;=dynlen(val); i++){
    table_data.cellValueRC(i-1, &quot;DT&quot;, dpValToString(dp[i], val[i]));
    if(val[i] == 0.0){
      table_data.cellBackColRC(i-1, &quot;DT&quot;, &quot;grey&quot;);
    }
  }
}

/*void sts_dt_int(dyn_string dp, dyn_anytype val){
  for(int i=1; i&lt;=dynlen(val); i++){
    table_data.cellValueRC(i-1, &quot;DTsts&quot;, dpValToString(dp[i], val[i]));
  }
}*/

void sts_dt(dyn_string dp, dyn_anytype val){
  for(int i=1; i&lt;=dynlen(val); i++){
    if(val[i] == 0x0){
      table_data.cellForeColRC(i-1, &quot;DT&quot;, &quot;STD_infoType_state_txt&quot;);
    }
    else{
      table_data.cellForeColRC(i-1, &quot;DT&quot;, &quot;{236,140,5}&quot;);
    }
  }
}

void cb_dp(dyn_string dp, dyn_anytype val){
  for(int i=1; i&lt;=dynlen(val); i++){
    table_data.cellValueRC(i-1, &quot;DP&quot;, dpValToString(dp[i], val[i]));
    if(val[i] == 0.0){
      table_data.cellBackColRC(i-1, &quot;DP&quot;, &quot;grey&quot;);
    }
  }
}

/*void sts_dp_int(dyn_string dp, dyn_anytype val){
  for(int i=1; i&lt;=dynlen(val); i++){
    table_data.cellValueRC(i-1, &quot;DPsts&quot;, dpValToString(dp[i], val[i]));
  }
}*/

void sts_dp(dyn_string dp, dyn_anytype val){
  for(int i=1; i&lt;=dynlen(val); i++){
    if(val[i] == 0x0){
      table_data.cellForeColRC(i-1, &quot;DP&quot;, &quot;STD_infoType_state_txt&quot;);
    }
    else{
      table_data.cellForeColRC(i-1, &quot;DP&quot;, &quot;{236,140,5}&quot;);
    }
  }
}

void worker_ls(dyn_string dp, dyn_bit32 sts){
  int ls1 = (uint)sts[1];
  rec_ls1.backCol = (ls1 &amp; 0x01 | ls1 &amp; 0x02 | ls1 &amp; 0x04) ? &quot;Red&quot; : &quot;Grey&quot;;
  if(dynlen(sts) == 2){
    int ls2 = (uint)sts[2];
    rec_ls2.backCol = (ls2 &amp; 0x01 | ls2 &amp; 0x02 | ls2 &amp; 0x04) ? &quot;Red&quot; : &quot;Grey&quot;;
  }
  else if(dynlen(sts) == 3){
   int ls2 = (uint)sts[2];
   int ls3 = (uint)sts[3];
   rec_ls2.backCol = (ls2 &amp; 0x01 | ls2 &amp; 0x02 | ls2 &amp; 0x04) ? &quot;Red&quot; : &quot;Grey&quot;;
   rec_ls3.backCol = (ls3 &amp; 0x01 | ls3 &amp; 0x02 | ls3 &amp; 0x04) ? &quot;Red&quot; : &quot;Grey&quot;;
 }
}

main(){

bool BtnVisible = 1;
  for(int i=1; i&lt;=16; i++) BtnVisible = BtnVisible &amp;&amp; getUserPermission(i);

  dyn_string dt_dps = makeDynString();
  dyn_string dp_dps = makeDynString();
  dyn_string dt_sts = makeDynString();
  dyn_string dp_sts = makeDynString();
  dyn_string dt_stsInt = makeDynString();
  dyn_string dp_stsInt = makeDynString();

  for(int i=1; i&lt;=21; i++)
    dynAppend(dt_dps, DP + &quot;.DT.&quot;+i+&quot;.val&quot;);
  for(int i=1; i&lt;=21; i++)
    dynAppend(dt_stsInt, DP + &quot;.DT.&quot;+i+&quot;.stsInt&quot;);
  for(int i=1; i&lt;=21; i++)
    dynAppend(dt_sts, DP + &quot;.DT.&quot;+i+&quot;.sts&quot;);

  for(int i=1; i&lt;=5; i++)
    dynAppend(dp_dps, DP + &quot;.DP.&quot;+i+&quot;.val&quot;);
  for(int i=1; i&lt;=5; i++)
    dynAppend(dp_stsInt, DP + &quot;.DP.&quot;+i+&quot;.stsInt&quot;);
  for(int i=1; i&lt;=5; i++)
    dynAppend(dp_sts, DP + &quot;.DP.&quot;+i+&quot;.sts&quot;);


  for(int i=1; i&lt;=21; i++)
    table_data.cellValueRC(i-1, &quot;#&quot;, i);
  lbl_comment.text(dpGetDescription(DP + &quot;.&quot;));

  dpConnect(&quot;cb_dt&quot;, dt_dps);
  dpConnect(&quot;cb_dp&quot;, dp_dps);
  dpConnect(&quot;sts_dt&quot;, dt_sts);
  dpConnect(&quot;sts_dp&quot;, dp_sts);
//  dpConnect(&quot;sts_dt_int&quot;, dt_stsInt);
//  dpConnect(&quot;sts_dp_int&quot;, dp_stsInt);

  dpConnect(&quot;worker_val&quot;, DP + &quot;.STS.val&quot;,               //1 - Состояние РВС(хран/отпуск..)
                          DP + &quot;.LVL.val&quot;,               //2 - Уровень продукта
                          DP + &quot;.DNS.val&quot;,               //3 - Плотность
                          DP + &quot;.VPR.PRS.val&quot;,           //4 - Давление паровой фазы продукта кПа
                          DP + &quot;.MAS.val&quot; ,              //5 - Масса
                          DP + &quot;.VOL.val&quot;,               //6 - Объём
                          DP + &quot;.WTR.val&quot;,               //7 - Уровень воды
                          DP + &quot;.TMP.val&quot;,               //8 - Температура
                          DP + &quot;.SCL.LSH&quot;,               //9 - Макс уровень
                          DP + &quot;.SCL.LSL&quot;,               //10 - Мин уровень
                          &quot;Logic&quot; + DP + &quot;.UnsanctionedChangeLevel:_alert_hdl.._act_state_color&quot;,  //11 - Несанкционированное изменение уровня
                          &quot;Logic&quot; + DP + &quot;.sTimeOtstoy&quot;     //12 - Оставшееся время отстоя
                          );

  dpConnect(&quot;cbLogicRVS&quot;, LogicRVS + &quot;.STS.status_rvs&quot;);

  if(LS3 != &quot;&quot; &amp; LS2 != &quot;&quot;){
    text_rec_ls1.text = LS1;
    text_rec_ls2.text = LS2;
    text_rec_ls3.text = LS3;
    rec_ls2.visible(true);
    rec_ls3.visible(true);
    text_rec_ls2.visible(true);
    text_rec_ls3.visible(true);
    dpConnect(&quot;worker_ls&quot;, LS1 + &quot;.status.sts_bit&quot;,
                           LS2 + &quot;.status.sts_bit&quot;,
                           LS3 + &quot;.status.sts_bit&quot;);
  }else if(LS2 != &quot;&quot;){
    text_rec_ls1.text = LS1;
    text_rec_ls2.text = LS2;
    rec_ls2.visible(true);
    rec_ls3.visible(false);
    text_rec_ls2.visible(true);
    text_rec_ls3.visible(false);
    dpConnect(&quot;worker_ls&quot;, LS1 + &quot;.status.sts_bit&quot;,
                           LS2 + &quot;.status.sts_bit&quot;);
  }else{
    text_rec_ls1.text = LS1;
    rec_ls2.visible(false);
    rec_ls3.visible(false);
    text_rec_ls2.visible(false);
    text_rec_ls3.visible(false);
    dpConnect(&quot;worker_ls&quot;, LS1 + &quot;.status.sts_bit&quot;);
  }

}

main()
{
  string fuel;
  dpGet(DP + &quot;.fuel_type&quot;, fuel);
  fuel_type.text(fuel);
}
