void cbWorker(dyn_string dp, dyn_int val){
ASN_00.visible(val[1]==0);
ASN_01.visible(val[1]==1);
ASN_02.visible(val[1]==2);
ASN_03.visible(val[1]==4);
ASN_04.visible(val[1]==8);
ASN_05.visible(val[1]==16);
if (val[1]==16) {
  RECTANGLE1.backCol(&quot;Red&quot;);
    }
else
{
  RECTANGLE1.backCol(&quot;WF_PanelBackground&quot;);
}
ASN_06.visible(val[1]==32);
ASN_07.visible(val[1]==64);
Zadanie.text(val[2]);
Prisadka.visible(val[3]==1);
}


