main()
{
  string type;
  int type_int;
  dpGet(ASN + &quot;.Post&quot; + POST + &quot;.Type_NP&quot;,type_int);
  switch(type_int){
    case 0:
      type = &quot;АИ-92&quot;;
      Prisadka.text(&quot;&quot;);
      break;
    case 1:
      type = &quot;АИ-95&quot;;
      Prisadka.text(&quot;HITEC&quot;);
      break;
    case 2:
      type = &quot;АИ-98&quot;;
      Prisadka.text(&quot;---&quot;);
      break;
    case 3:
      type = &quot;АИ-100&quot;;
      Prisadka.text(&quot;---&quot;);
      break;
    case 4:
      type = &quot;ДТ&quot;;
      Prisadka.text(&quot;FC9000G&quot;);
      break;
    default:
      type = &quot;---&quot;;
  }
  type_np.text(type);
  Post_num.text = &quot;Пост №&quot;+POST;
  dpConnect(&quot;cbWorker&quot;, ASN + &quot;.Post&quot; +POST + &quot;.sStatusPosta&quot;,
            ASN + &quot;.Post&quot; +POST + &quot;.cVolumeDose&quot;,
            ASN + &quot;.Post&quot; +POST + &quot;.сPrisadka&quot;);


/*
  dpConnect(&quot;cbAlarm&quot;, LogicRVS + &quot;.&quot; + ANALOG + &quot;._sAH_Level:_alert_hdl.._act_state_color&quot;,
